function average(e, t, n, E, u, T, d, I, A, a, r, m, M, N) {
    return (1 * e + 1 * t + 1 * n + 1 * E + 1 * u + 1 * T + 1 * d + 1 * I + 1 * A + 1 * a + 1 * r + 1 * m + 1 * M + 1 * N) / 14
}
if($('#result').length){
    document.getElementById("result").onclick = function () {
        var e = document.getElementById("range").value,
            t = document.getElementById("range2").value,
            n = document.getElementById("range3").value,
            E = document.getElementById("range4").value,
            u = document.getElementById("range5").value,
            T = document.getElementById("range6").value,
            d = document.getElementById("range7").value,
            I = document.getElementById("range8").value,
            A = document.getElementById("range9").value,
            a = document.getElementById("range10").value,
            r = document.getElementById("range11").value,
            m = document.getElementById("range12").value,
            M = document.getElementById("range13").value,
            N = document.getElementById("range14").value;

        var total = Math.round(average(e, t, n, E, u, T, d, I, A, a, r, m, M, N));
        $("#total").html(total)

        console.log(total)
        //FUNCTIONAL CODE
        function symptom() {
            if (total < 19) {
                $(".main-result p:nth-child(2)").addClass("p-child");

            } else if (total > 19 && total < 39) {
                $(".main-result p:nth-child(3)").addClass("p-child");

            } else if (total > 39 && total < 59) {
                $(".main-result p:nth-child(4)").addClass("p-child");

            } else if (total > 59 && total < 79) {
                $(".main-result p:nth-child(5)").addClass("p-child");

            } else {
                $(".main-result p:nth-child(6)").addClass("p-child");
            }
        }

        symptom();
    };
}